mapper.select:
SELECT 

FROM accountant_book a

WHERE a.id = #{id}

SELECT 

FROM accountant_book a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.parent_id = #{parent.id}


		AND a.parent_ids LIKE 
			'%'||#{parentIds}||'%'
			'%'+#{parentIds}+'%'
			concat('%',#{parentIds},'%')


		AND a.code = #{code}


		AND a.user_id = #{user.id}


		AND a.company_id = #{companyId}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.assist_code LIKE 
			'%'||#{assistCode}
			'%'+#{assistCode}
			concat('%',#{assistCode})


		AND a.category = #{category}


		AND a.accountant_category = #{accountantCategory}


		AND a.property = #{property}


		AND a.type = #{type}


ORDER BY a.sort ASC

SELECT
	a.id,
	a.name,
	a.assist_code AS "assistCode",
	a.category AS "category",
	a.accountant_category AS "accountantCategory",
	a.parent_id AS "parent.id",
	a.parent_ids
FROM accountant_book a


	a.del_flag = #{DEL_FLAG_NORMAL}


		AND a.category = #{category}



		AND a.accountant_category = #{accountantCategory}



		AND a.final_stage = #{finalStage}



		AND a.company_id = #{companyId}



ORDER BY a.sort ASC

mapper.update:
UPDATE accountant_book SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}

UPDATE accountant_book SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id} OR parent_ids LIKE '%,'||#{id}||',%'
