mapper.select:
SELECT
	sum(IF(a.direction = 'left',a.amount,0)) leftSumAmount , sum(IF(a.direction = 'right',a.amount,0)) rightSumAmount
FROM accountant_book_record_detail a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND c.category = #{book.category}

SELECT
	sum(IF(a.direction = 'left',a.amount,0)) leftSumAmount , sum(IF(a.direction = 'right',a.amount,0)) rightSumAmount
FROM accountant_book_record_detail a

	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.book_id = #{bookId}

SELECT

FROM accountant_book_record_detail a

WHERE a.id = #{id}

SELECT 

FROM accountant_book_record_detail a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.record_id = #{record.id}


		AND a.book_id = #{book.id}




		ORDER BY ${page.orderBy}


		ORDER BY record_timestamp ASC

SELECT 

FROM accountant_book_record_detail a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY record_timestamp DESC

SELECT

FROM accountant_book_record_detail a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.book_id = #{bookId}


		AND a.bookrecord_type = #{bookRecordType}


		AND a.DATE_FORMAT(a.record_date,'%Y-%m') = #{recDate}




		ORDER BY ${page.orderBy}


		ORDER BY record_timestamp DESC

SELECT a.id AS "id", c.category AS "book.category", a.create_date AS "createDate", b.digest AS "record.digest", a.amount AS "amount", a.direction , CASE c.category WHEN 'left' THEN (select sum(IF(br.direction = 'left',br.amount,0)) - sum(IF(br.direction = 'right',br.amount,0)) from accountant_book_record_detail br WHERE br.record_timestamp <= a.record_timestamp AND br.book_id=a.book_id) WHEN 'right' THEN (select sum(IF(br.direction = 'right',br.amount,0)) - sum(IF(br.direction = 'left',br.amount,0)) from accountant_book_record_detail br WHERE br.record_timestamp <= a.record_timestamp AND br.book_id=a.book_id) END AS balance, a.record_id AS "record.id", a.book_id AS "bookId"
FROM accountant_book_record_detail a
a.del_flag = 0
AND a.book_id = #{bookId}
ORDER BY ${page.orderBy}
ORDER BY record_timestamp DESC

SELECT

FROM accountant_book_record_detail a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.book_id = #{bookId}


	  ORDER BY record_timestamp DESC limit 0,1

mapper.update:
UPDATE accountant_book_record_detail SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE record_id = #{record.id}
